import numpy

class IndexedCoord:

    def __init__(self, index:int, x:float, y:float):
        self.index = index
        self.x = x
        self.y = y

    def is_less_than(self, other):
        return self.x < other.x and self.y < other.y

    def is_greater_than(self, other):
        return self.x > other.x and self.y > other.y

    def get_halway_point(self, other):
        min_x = min(self.x, other.x)
        max_x = max(self.x, other.x)
        min_y = min(self.y, other.y)
        max_y = max(self.y, other.y)

        return IndexedCoord(0, min_x + ((max_x - min_x) / 2), min_y + ((max_y - min_y) / 2))

    def __str__(self):
        return "index:{0}, x:{1}, y:{2}".format(self.index, self.x, self.y)



